import { Outlet, Link } from 'react-router-dom';
import React, { useState } from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Image from 'react-bootstrap/Image';
import Button from 'react-bootstrap/Button';
import Collapse from 'react-bootstrap/Collapse';
import NDSlogo from '../Assets/Images/nds-logo.webp';
import { ChevronDownIcon, Cog8ToothIcon, PlusCircleIcon, Bars3Icon } from '@heroicons/react/24/outline';

const Layout = () => {
  const [open, setOpen] = useState(false);
  return (
    <Container fluid className='h-100'>
      <Row className='h-100'>
        {/* Sidebar */}
        <Col md={3} xl={2} className='layout d-flex flex-column d-block p-3 sidebar'>
          <Col className='layout__top'>
            <div className='d-flex justify-content-between'>
              <Image fluid src={NDSlogo} className='logo-nav' />
              <a href="">
                <Bars3Icon className='icon my-auto d-md-none'></Bars3Icon>
              </a>


            </div>
            <div className='py-4'> Bienvenido, User! </div>
            <hr className='hr-style' />
            <nav className='py-2'>
              <ul>
                <li className='py-1'>
                  <Button
                    variant='light'
                    className='d-flex justify-content-between py-2 w-100 px-0'
                    onClick={() => setOpen(!open)}
                    aria-controls='example-collapse-text'
                    aria-expanded={open}
                  >
                    <span> Zona Sur </span> <ChevronDownIcon className='icon my-auto'></ChevronDownIcon>
                  </Button>
                  <Collapse in={open}>
                    <Link to='/tienda'>
                      <Button variant='light' className='text-start py-2 w-100'>
                        Oxxo
                      </Button>
                    </Link>
                  </Collapse>
                </li>
              </ul>
            </nav>
          </Col>
          <Col className='layout__bottom'>
            <Col>
              <Button variant='light' className='d-flex justify-content-between py-3 w-100 px-0'>
                <span className='my-auto'>Editar locales</span>
                <Cog8ToothIcon className='icon my-auto'></Cog8ToothIcon>
              </Button>
              <hr className='hr-style' />
              <Button variant='light' className='d-flex justify-content-between py-3 w-100 px-0'>
                <span className='my-auto'>Agregar local</span>
                <PlusCircleIcon className='icon my-auto'></PlusCircleIcon>
              </Button>
            </Col>
          </Col>
        </Col>
        {/* Content */}
        <Col md={9} xl={10} className='p-5 content'>
          <Outlet />
        </Col>
      </Row>
    </Container>
  );
};

export default Layout;
